package com.zaz.ssapi.protocol.tn3270.util;

/**
 *
 * @author tianlize
 */
public class RowColAddress {

    private int row = 0;
    private int col = 0;

    public RowColAddress(int row, int col) {
        this.row = row;
        this.col = col;
    }

    public int getCol() {
        return col;
    }

    public void setCol(int col) {
        this.col = col;
    }

    public int getRow() {
        return row;
    }

    public void setRow(int row) {
        this.row = row;
    }

    @Override
    public String toString() {
        return "[" + row + "," + col + "]";
    }
}
