def groovyUtils = new com.eviware.soapui.support.GroovyUtils (context);
def holder = groovyUtils.getXmlHolder ("GetTaskList - Request 1#Response");
holder.declareNamespace("task", "http://jbi.com.sun/wfse/wsdl/TaskCommon");
def taskId = holder.getNodeValue("//task:task[1]/task:taskId");
log.info ("Task Id: " + taskId);
groovyUtils.setPropertyValue ("Properties", "taskId", taskId);

import javax.xml.parsers.DocumentBuilderFactory;
import javax.xml.transform.OutputKeys;
import javax.xml.transform.Result;
import javax.xml.transform.Transformer;
import javax.xml.transform.TransformerFactory;
import javax.xml.transform.dom.DOMSource;
import javax.xml.transform.stream.StreamResult;

def groovyUtils = new com.eviware.soapui.support.GroovyUtils (context);
def holder = groovyUtils.getXmlHolder ("GetXform#Response");
holder.declareNamespace('xhtml', 'http://www.w3.org/1999/xhtml');
def xhtml_top = holder.getDomNode("//xhtml:html");

def baos = new ByteArrayOutputStream();
def osw = new OutputStreamWriter(baos, "UTF-8");
def result = new StreamResult(osw);

// Write the DOM document to the file
def xformer = TransformerFactory.newInstance().newTransformer();
xformer.setOutputProperty(OutputKeys.ENCODING, "UTF-8");
xformer.transform(new DOMSource(xhtml_top), result);
def ret = baos.toString("UTF-8");


outputFileName = '/home/mei/work/open-jbi-components/driver-tests/wlmse/Basic_User_Group/PurchaseOrderCompositeApp/test/soapui/output/PurchaseOrderXform.xhtml'

def utf8Bytes = ret.getBytes("UTF8");
def fout= new FileOutputStream(outputFileName);


fout.write(utf8Bytes);
fout.flush();
fout.close();

outputFileName = '/home/mei/work/open-jbi-components/driver-tests/wlmse/Basic_User_Group/PurchaseOrderCompositeApp/test/soapui/output/PurchaseOrderOutput.xml'
content = new File(outputFileName).text
def po = new groovy.xml.Namespace('http://wlmse.sample/po', 'po')
def orderReply = new groovy.util.XmlParser().parseText(content)
if  ('Approved' != orderReply[po.approveResult].text()) 
	 throw new Exception ("Result from BPEL does not match!");

def groovyUtils = new com.eviware.soapui.support.GroovyUtils (context);
def holder = groovyUtils.getXmlHolder ("GetTaskList - Request 1#Response");
holder.declareNamespace("task", "http://jbi.com.sun/wfse/wsdl/TaskCommon");
def taskId = holder.getNodeValue("//task:task[1]/task:taskId");
def taskCount = holder.getNodeValue("count(//task:task)");
log.info ("Task Id: " + taskId);
log.info("Task count:" + taskCount);
groovyUtils.setPropertyValue ("Properties", "taskId", taskId);
groovyUtils.setPropertyValue ("Properties", "taskCount", taskCount);

