def groovyUtils = new com.eviware.soapui.support.GroovyUtils (context);
def holder = groovyUtils.getXmlHolder ("GetTaskForJohn#Response");
holder.declareNamespace("task", "http://jbi.com.sun/wfse/wsdl/TaskCommon");
def taskCount = holder.getNodeValue("//task:totalRecords");
def taskEscalated = holder.getNodeValue("count(//task:task[task:status='ESCALATED'])");
log.info("Escalated task:" + taskEscalated);

log.info("Task count:" + taskCount);
assert taskCount.toInteger() > 0;

def taskId = holder.getNodeValue("//task:task[task:title='vacation request for Linda Chan'][task:status='ASSIGNED'][1]/task:taskId");
log.info ("Task Id: " + taskId);
groovyUtils.setPropertyValue ("Properties", "taskId", taskId);
groovyUtils.setPropertyValue ("Properties", "taskEscalated", taskEscalated);
groovyUtils.setPropertyValue ("Properties", "taskCount", taskCount);

def groovyUtils = new com.eviware.soapui.support.GroovyUtils (context);
def holder = groovyUtils.getXmlHolder ("GetTaskForJohn2#Response");
holder.declareNamespace("task", "http://jbi.com.sun/wfse/wsdl/TaskCommon");
def taskCount = holder.getNodeValue("//task:totalRecords");
log.info("Task count:" + taskCount);


def taskCountOld = groovyUtils.expand('${taskCount}');
log.info("Task old count:" + taskCountOld);

assert taskCount.toInteger() == taskCountOld.toInteger();

def taskEscalated = holder.getNodeValue("count(//task:task[task:status='ESCALATED'])");
log.info("Escalated task:" + taskEscalated);

def taskEscCountOld = groovyUtils.expand('${taskEscalated}');
log.info("Task old escalated count:" + taskEscCountOld);

assert taskEscalated.toInteger () > taskEscCountOld.toInteger();

def groovyUtils = new com.eviware.soapui.support.GroovyUtils (context);
def holder = groovyUtils.getXmlHolder ("GetTaskForDale#Response");
holder.declareNamespace("task", "http://jbi.com.sun/wfse/wsdl/TaskCommon");
def taskId = groovyUtils.expand('${taskId}');
def taskNode = holder.getDomNode("//task:task[task:taskId =" + taskId+"]");
assert taskNode != null
