package com.sun.wsdl.model.uri;

/**
 * Resolver to resolve a SystemID to a file in local file system if the mapping presents *
 * 
 * @author Sun Microsystems
 * @version 
 *
 */
public interface CatalogResolver {
    /**
     * Returns the localuri if a mapping presents in catalog, otherwise returns the systemId passed in
     * @param systemId systemId
     * @return localuri or systemId
     */
    String resolve (String systemId);
}
