SELECT HL7DataStructures.elementary, HL7DataStructureComponents.table_id, HL7DataStructureComponents.seq_no, HL7Components.data_structure, HL7Components.description as subdesc, HL7Components.data_type_code FROM (((HL7Versions INNER JOIN HL7DataStructureComponents ON (HL7Versions.version_id = HL7DataStructureComponents.version_id)) INNER JOIN HL7Components ON (HL7DataStructureComponents.comp_no = HL7Components.comp_no) AND (HL7DataStructureComponents.version_id = HL7Components.version_id)) INNER JOIN HL7DataStructures ON (HL7Versions.version_id = HL7DataStructures.version_id) AND (HL7Components.data_structure = HL7DataStructures.data_structure)) WHERE (((HL7Versions.hl7_version)= ? ) AND ((HL7DataStructureComponents.data_structure)= ? )) ORDER BY HL7DataStructureComponents.seq_no
