SELECT HL7EventMessageTypeSegments.seg_code, HL7EventMessageTypeSegments.seq_no, HL7EventMessageTypeSegments.groupname, HL7EventMessageTypeSegments.usage, HL7EventMessageTypeSegments.repetitional, HL7EventMessageTypeSegments.optional FROM HL7Versions INNER JOIN HL7EventMessageTypeSegments ON HL7Versions.version_id = HL7EventMessageTypeSegments.version_id WHERE (((HL7Versions.hl7_version)= ? ) AND ((HL7EventMessageTypeSegments.message_type)= ? ) AND ((HL7EventMessageTypeSegments.event_code)= ? )) ORDER BY HL7EventMessageTypeSegments.seq_no
