SELECT HL7MsgStructIDSegments.seg_code, HL7MsgStructIDSegments.seq_no, HL7MsgStructIDSegments.groupname, HL7MsgStructIDSegments.usage, HL7MsgStructIDSegments.repetitional, HL7MsgStructIDSegments.optional FROM (HL7Versions INNER JOIN HL7MsgStructIDs ON HL7Versions.version_id = HL7MsgStructIDs.version_id) INNER JOIN HL7MsgStructIDSegments ON (HL7MsgStructIDs.message_structure = HL7MsgStructIDSegments.message_structure) AND (HL7MsgStructIDs.version_id = HL7MsgStructIDSegments.version_id) WHERE (((HL7Versions.hl7_version)= ? ) AND ((HL7MsgStructIDs.message_structure)= ? )) ORDER BY HL7MsgStructIDSegments.seq_no
