package com.sun.jbi.hl7bc.util;

import java.io.PrintWriter;
import java.io.StringWriter;

import java.util.logging.Level;

public class LogSupport {

    public static final Level LEVEL_DEBUG = Level.FINE;

	public static final Level LEVEL_INFO = Level.INFO;
    
    /**
     * Returns the stack trace of a Throwable as a String
     * @param t Instance of Throwable
     * @return The stack trace of t as a String
     */
    public static String getStackTraceAsString(Throwable t) {
        StringWriter sw = new StringWriter();
        PrintWriter pw = new PrintWriter(sw);
        t.printStackTrace(pw);
        return sw.toString();
    }
}
