package com.sun.jbi.msmqbc.jni;

/**
 * This class wraps the share modes for opening a queue
 *  Values must match those in mq.h
 *
 * @author Sun Microsystems
 */

public class MSMQShareMode {
	public static final int DENY_NONE          = 0x00000000;
	public static final int DENY_RECEIVE_SHARE = 0x00000001;
}
